package graphicsimport org.lwjgl.opengl.GL15import org.lwjgl.opengl.GL20import org.lwjgl.opengl.GL30.*class Point(vertices: FloatArray) {        private val vao = glGenVertexArrays()    private val vbo = glGenBuffers()        private var count = vertices.size        init {        glBindVertexArray(vao)        glBindBuffer(GL_ARRAY_BUFFER, vbo)        GL20.glVertexAttribPointer(0, 2, GL_FLOAT, false, 0, 0)        glEnableVertexAttribArray(0)                GL15.glBufferData(GL_ARRAY_BUFFER, vertices, GL_STATIC_DRAW)        glBindBuffer(GL_ARRAY_BUFFER, 0)        glBindVertexArray(0)    }        fun render() {        glBindVertexArray(vao)        glDrawArrays(GL_POINTS, 0, count)        glBindVertexArray(0)    }}