package graphicsimport org.lwjgl.opengl.GL11import org.lwjgl.opengl.GL30.*class PointMesh(point: FloatArray) {        private val vao = glGenVertexArrays()    private val vbo = glGenBuffers()        init {        glBindVertexArray(vao)        glBindBuffer(GL_ARRAY_BUFFER, vbo)        glVertexAttribPointer(0, 2, GL_FLOAT, false, 0, 0)        glEnableVertexAttribArray(0)        glBufferData(GL_ARRAY_BUFFER, point, GL_STATIC_DRAW)        glBindBuffer(GL_ARRAY_BUFFER, 0)        glBindVertexArray(0)    }        fun draw() {        glBindVertexArray(vao)        glDrawArrays(GL11.GL_POINTS, 0, 1)        glBindVertexArray(0)    }        fun destroy() {        glDeleteBuffers(vbo)        glDeleteVertexArrays(vao)    }    }