package userinterface.animation.effectsimport math.Colorimport userinterface.UIColorimport userinterface.UniversalParameters.ANIMATION_DURATIONimport userinterface.animation.ColorAnimationimport userinterface.items.Itemimport userinterface.items.backgrounds.ColorTypeclass ColorEffect(private val changeToColor: Color, private val colorType: ColorType, duration: Float = ANIMATION_DURATION) : Effect(duration) {        constructor(uiColor: UIColor, colorType: ColorType, duration: Float = ANIMATION_DURATION) : this(uiColor.color, colorType, duration)        override fun applyOn(item: Item) {        item.animator += ColorAnimation(duration, changeToColor, colorType, item)    }        override fun removeFrom(item: Item) {        item.animator += ColorAnimation(duration, item.baseBackgroundColor, colorType, item)    }}