package userinterface.itemsimport userinterface.UniversalParametersimport userinterface.animation.Animatorimport userinterface.items.backgrounds.Backgroundimport userinterface.layout.constraints.ConstraintSetclass CheckBox(id: String, constraints: ConstraintSet, var checked: Boolean = false, var onCheck: (Boolean) -> Unit = {}, background: Background = UniversalParameters.CHECK_BOX_UNCHECKED_BACKGROUND()) : UIButton(id, constraints, {}, background = background) {        init {        if (checked) {            this.background = UniversalParameters.CHECK_BOX_CHECKED_BACKGROUND()        } else {            this.background = UniversalParameters.CHECK_BOX_UNCHECKED_BACKGROUND()        }        setOnClick {            if (checked) {                uncheck()            } else {                check()            }            onCheck(checked)        }    }        fun check() {        checked = true        background = UniversalParameters.CHECK_BOX_CHECKED_BACKGROUND()    }        fun uncheck() {        checked = false        background = UniversalParameters.CHECK_BOX_UNCHECKED_BACKGROUND()    }    }