package userinterface.svgimport graphics.shaders.ShaderProgramimport math.vectors.Vector2import userinterface.UIColorimport kotlin.math.minclass SVGIcon(private val mesh: SVGMesh, var size: Float = 1.0f, private var translation: Vector2 = Vector2()) {    fun translate(translation: Vector2) {        this.translation=translation        println(translation)    }    fun setScale(scale: Float) {        val minSize = min(mesh.xSize, mesh.ySize)        println("minSize: $minSize, ${scale}")        size = scale    }    fun draw(shaderProgram: ShaderProgram) {        shaderProgram.set("isIcon", true)        shaderProgram.set("size", size)        shaderProgram.set("translation", translation)        shaderProgram.set("color", UIColor.WHITE.color)        mesh.draw()    }    }