package userinterface.svgimport graphics.shaders.ShaderProgramimport math.vectors.Vector2import userinterface.UIColorclass SVGIcon(val mesh: SVGMesh, val size: Float = 1.0f, val translation: Vector2 = Vector2()) {        fun draw(shaderProgram: ShaderProgram) {        shaderProgram.set("isIcon", true)        shaderProgram.set("size", 0.1f)        shaderProgram.set("translation", translation)        shaderProgram.set("color", UIColor.WHITE.color)        mesh.draw()    }    }