package userinterface.textimport devices.Keyenum class Characters(val key: Key, val mainCharacter: Char, val alternativeCharacter: Char? = null) {        ZERO(Key.ZERO, '0', ')'),    ONE(Key.ONE, '1', '!'),    TWO(Key.TWO, '2', '@'),    THREE(Key.THREE, '3', '#'),    FOUR(Key.FOUR, '4', '$'),    FIVE(Key.FIVE, '5', '%'),    SIX(Key.SIX, '6', '^'),    SEVEN(Key.SEVEN, '7', '&'),    EIGHT(Key.EIGHT, '8', '*'),    NINE(Key.NINE, '9', '('),        A(Key.A, 'a', 'A'),    B(Key.B, 'b', 'B'),    C(Key.C, 'c', 'C'),    D(Key.D, 'd', 'D'),    E(Key.E, 'e', 'E'),    F(Key.F, 'f', 'F'),    G(Key.G, 'g', 'G'),    H(Key.H, 'h', 'H'),    I(Key.I, 'i', 'I'),    J(Key.J, 'j', 'J'),    K(Key.K, 'k', 'K'),    L(Key.L, 'l', 'L'),    M(Key.M, 'm', 'M'),    N(Key.N, 'n', 'N'),    O(Key.O, 'o', 'O'),    P(Key.P, 'p', 'P'),    Q(Key.Q, 'q', 'Q'),    R(Key.R, 'r', 'R'),    S(Key.S, 's', 'S'),    T(Key.T, 't', 'T'),    U(Key.U, 'u', 'U'),    V(Key.V, 'v', 'V'),    W(Key.W, 'w', 'W'),    X(Key.X, 'x', 'X'),    Y(Key.Y, 'y', 'Y'),    Z(Key.Z, 'z', 'Z'),        SPACE(Key.SPACE, ' '),    SLASH(Key.SLASH, '/', '?'),    SEMICOLON(Key.SEMICOLON, ';', ':'),    COMMA(Key.COMMA, ',', '<'),    PERIOD(Key.PERIOD, '.', '>'),    APOSTROPHE(Key.APOSTROPHE, '\'', '\"')    }